# Покупки на сегодняtoday = ["лемон", "блокнот", "кола", "ручка", "сигареты", "молоко", "лампочка"]# Покупки на завтраtomorrow = []# Удалил сигареты из списка today (мы за ЗОЖ)today.remove("сигареты")# Добавил все орехи из списка nuts в список todaynuts = ["фисташки", "грецкие орехи"]today.extend(nuts)# Заменил колу на максым (максым сила!)today[2]="максым"# Вставил миндаль между фисташками и грецкими орехамиtoday.insert(-1,"миндаль")# Добавил курут в конец списка todaytoday.append("курут")# Всё не съедобное переместил в покупки на завтра# Тут я соеденил метод append и pop последовательно, потому что pop работал не корректноtomorrow.append(today.pop(1))tomorrow.append(today.pop(2))tomorrow.append(today.pop(3))# В итоге мы получили полностью полностью сформированные спискиprint(today) # ['лемон', 'максым', 'молоко', 'фисташки', 'миндаль', 'грецкие орехи', 'курут']print(tomorrow) # ['блокнот', 'ручка', 'лампочка']